package oop;

public class BitwiseOperatior {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		int b=3;
		// and operator &
		int c=a & b;
		// or operator |
		int d=a | b;
		// xor  ^ 
		int e= a ^ b;
		// not ~
		int f=~a;
		// left shift <<-
		int leftShift=a << 2;
		// right shift >>
		int rightShift= a>> 2;
		// unsigned right shift >>>
		int unRightShift= a >>> 2;
	}

}
